import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  generateRandomKey = () => {
    const LETTERS = ['a', 'b', 'c','d','e'];
    const NUMBER = Math.floor((Math.random() * 100) + 1);

    return NUMBER + LETTERS[Math.floor((Math.random() * 4)+1)];
  }

  state = {
    persons: [
      {name:'Modestas', age:25, id: this.generateRandomKey()},
      {name:'Tadas', age:55, id: this.generateRandomKey()}
    ],
    showPersons: false,
    newPersonName: 'new Person'
  };

  showPersonsHandler = () => {
    const show = this.state.showPersons;
    this.setState({showPersons : !show});
  }

  deletePersonHandler = (personIndex) => {
    const persons = this.state.persons;
    persons.splice(personIndex, 1);
    this.setState({
      persons : persons
    });
  };

  addPersonHandler = () => {
    const persons = this.state.persons;
    persons.push(
       {name: this.state.newPersonName , age : 0, id: this.generateRandomKey()}
    );
    this.setState({
      persons : persons
    });
  }

  updateNewPersonsNameHandler = (event) => {
    this.setState({newPersonName: event.target.value})
  }

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        {name: event.target.value, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };

  render() {
    let persons = (
      <p> No persons </p>
    )

    if(this.state.persons.length > 0){
      persons = (
        <div className="d-flex">
          {this.state.persons.map((person, index) => {
            return (<Person
              click = {() => this.deletePersonHandler(index)}
              name={person.name} 
              age={person.age}
              id={person.id}
              key={person.id}
            />)  
          })}    
        </div>  
      )   
    };

    return (
      <section> 
        <div className="App">
          <form>
            <input
              onChange={this.updateNewPersonsNameHandler}
              className='p-10' 
              type='text' id='name' 
              placeholder={this.state.newPersonName} 
            />
          </form>

          <button className='' onClick={this.addPersonHandler}>Add Person</button>
          {persons}
        </div>
      </section>
    );
  }
}

export default App;
