import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      {name:'Modestas', age:25},
      {name:'Tadas', age:55}
    ],
    showPersons: false,
    newPersonName: 'new Person'
  };

  showPersonsHandler = () => {
    const show = this.state.showPersons;
    this.setState({showPersons : !show});
  }

  deletePersonHandler = (personIndex) => {
    const persons = this.state.persons;
    persons.splice(personIndex, 1);
    this.setState({
      persons : persons
    });
  };

  addPersonHandler = () => {
    const persons = this.state.persons;
    persons.push(
       {name: 'New Person', age : 0}
    );
    this.setState({
      persons : persons
    });
  }

  updateNewPersonsNameHandler = (event) => {

  }

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        {name: event.target.value, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };

  render() {
    const style = {
      border: '1px solid green',
      padding: '10px',
      backgroundColor: 'white',
      cursor: 'pointer'
    };

    let persons = (
      <p> No persons </p>
    )

    if(this.state.persons.length > 0){
      persons = (
        <div className="d-flex">
          {this.state.persons.map((person, index) => {
            return (<Person
              click = {() => this.deletePersonHandler(index)}
              name={person.name} 
              age={person.age}
            />)  
          })}    
        </div>  
      )   
    };

    return (
      <div className="App">
        <form>
          <input
            onChange={this.updateNewPersonsNameHandler}
            className='p-10' 
            type='text' id='name' 
            placeholder={this.state.newPersonName} 
          />

          <button className='m-10' style={style} onClick={this.addPersonHandler}>Add Person</button>
        </form>
        {persons}

      </div>
    );
  }
}

export default App;
