import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      {name:'Modestas', age:25},
      {name:'Tadas', age:55}
    ],
    showPersons: false
  };

  switchNameHandler = (thisName) => {
    this.setState({
      persons: [
        {name: thisName, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        {name: event.target.value, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };

  render() {
    const style = {
      border: '1px solid green',
      padding: '10px',
      backgroundColor: 'white',
      cursor: 'pointer'
    };

    return (
      <div className="App">
        <button style={style} onClick={this.switchNameHandler.bind(this, 'Modestas!!!')}>Switch Name</button>

        <div className="d-flex">
          <Person
          name={this.state.persons[0].name} 
          age={this.state.persons[0].age}
          click={this.switchNameHandler.bind(this, 'Modestas New!!!')}
          change={this.nameChangeHandler} />
          <Person 
            name={this.state.persons[1].name} 
            age={this.state.persons[1].age}> 
            Is an old person 
          </Person>   
        </div>

      </div>
    );
  }
}

export default App;
