import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      {name:'Modestas', age:25},
      {name:'Tadas', age:55}
    ],
    showPersons: false
  };

  showPersonsHandler = () => {
    const show = this.state.showPersons;
    this.setState({showPersons : !show});
  }

  renderPersons = () => {
    this.state.persons.forEach((person, index) => {
      return (
        <Person
            name={person[index].name} 
            age={person[index].age}
        />
      )
    })
  };

  switchNameHandler = (thisName) => {
    this.setState({
      persons: [
        {name: thisName, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        {name: event.target.value, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };

  render() {
    const style = {
      border: '1px solid green',
      padding: '10px',
      backgroundColor: 'white',
      cursor: 'pointer'
    };

    return (
      <div className="App">
        <button style={style} onClick={this.showPersonsHandler}>Switch Name</button>

        { this.state.showPersons === true ?
          <div className="d-flex">
            renderPersons()
          </div> : null
        }


      </div>
    );
  }
}

export default App;
