import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [

    ],
    showPersons: false
  };

  showPersonsHandler = () => {
    const show = this.state.showPersons;
    this.setState({showPersons : !show});
  }

  switchNameHandler = (thisName) => {
    this.setState({
      persons: [
        {name: thisName, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        {name: event.target.value, age:65},
        {name:'Tadas', age:55}
      ]
    })
  };



  render() {
    const style = {
      border: '1px solid green',
      padding: '10px',
      backgroundColor: 'white',
      cursor: 'pointer'
    };

    let persons = (
      <p> No persons </p>
    )

    if(this.state.persons.length > 0){
      persons = (
        <div className="d-flex">
          {this.state.persons.map(person => {
            return (<Person 
              name={person.name} 
              age={person.age}
            />)  
          })}    
        </div>  
      )   
    };

    return (
      <div className="App">
        <button style={style} onClick={this.showPersonsHandler}>Switch Name</button>

        {persons}

      </div>
    );
  }
}

export default App;
